import React, { useState } from "react";
import { useAuth } from "../../services/auth/AuthContext";
import "./Books.css";

const BookCard = ({ book, onReserve, onLoan, showActions = true }) => {
  const { user } = useAuth();  // <-- CAMBIAR AQUÍ
  const [isLoading, setIsLoading] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);

  if (!book) return null;

  const handleReserve = async () => {
    setIsLoading(true);
    try {
      await onReserve(book.id);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLoan = async () => {
    setIsLoading(true);
    try {
      await onLoan(book.id);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="book-card card h-100">
      <div className="card-body">
        <h5 className="card-title">{book.title}</h5>
        <h6 className="card-subtitle mb-2 text-muted">
          {book.authors?.map(a => a.name).join(", ")}
        </h6>

        <p className="card-text">
          <small className="text-muted">
            ISBN: {book.isbn}<br/>
            Año: {book.publicationYear}<br/>
            Disponibles: {book.availableCopies}/{book.totalCopies}
          </small>
        </p>

        {showActions && user && (
          <div className="mt-3">
            {book.availableCopies > 0 ? (
              <button
                className="btn btn-primary btn-sm me-2"
                onClick={handleLoan}
                disabled={isLoading}
              >
                {isLoading ? "Procesando..." : "Solicitar Préstamo"}
              </button>
            ) : (
              <button
                className="btn btn-warning btn-sm me-2"
                onClick={handleReserve}
                disabled={isLoading}
              >
                {isLoading ? "Procesando..." : "Reservar"}
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default BookCard;
