import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../services/auth/AuthContext';
import LoadingSpinner from '../components/common/LoadingSpinner';
import './LoginPage.css';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [loginError, setLoginError] = useState('');
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.email) {
      newErrors.email = 'El email es requerido';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email inv√°lido';
    }
    
    if (!formData.password) {
      newErrors.password = 'La contrase√±a es requerida';
    } else if (formData.password.length < 6) {
      newErrors.password = 'La contrase√±a debe tener al menos 6 caracteres';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setLoading(true);
    setLoginError('');
    
    try {
      // Simulaci√≥n de login
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      const mockUser = {
        id: 1,
        email: formData.email,
        name: 'Usuario Demo',
        role: 'USER',
        token: 'mock-jwt-token'
      };
      
      login(mockUser);
      navigate('/dashboard');
      
    } catch (error) {
      setLoginError('Credenciales inv√°lidas. Por favor, int√©ntalo de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleForgotPassword = () => {
    alert('Funcionalidad de recuperaci√≥n de contrase√±a en desarrollo');
  };

  const handleDemoLogin = (role) => {
    setFormData({
      email: `${role.toLowerCase()}@demo.com`,
      password: 'demo123',
      rememberMe: false
    });
    
    // Auto-submit after a delay
    setTimeout(() => {
      handleSubmit({ preventDefault: () => {} });
    }, 500);
  };

  return (
    <div className="login-page">
      <div className="login-container">
        {/* Left Panel - Branding */}
        <div className="login-left">
          <div className="brand-section">
            <div className="brand-logo">
              <span className="logo-icon">üìö</span>
              <span className="logo-text">Biblioteca Digital</span>
            </div>
            <h1 className="brand-title">Bienvenido de vuelta</h1>
            <p className="brand-subtitle">
              Accede a tu cuenta para gestionar pr√©stamos, reservas y explorar nuestro cat√°logo
            </p>
          </div>
          
          <div className="features-list">
            <div className="feature-item">
              <span className="feature-icon">üìñ</span>
              <span className="feature-text">Accede a miles de libros</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">‚è∞</span>
              <span className="feature-text">Gestiona tus pr√©stamos 24/7</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">üîî</span>
              <span className="feature-text">Recibe notificaciones</span>
            </div>
            <div className="feature-item">
              <span className="feature-icon">‚≠ê</span>
              <span className="feature-text">Sistema de recomendaciones</span>
            </div>
          </div>
          
          <div className="stats-section">
            <div className="stat">
              <span className="stat-number">10,000+</span>
              <span className="stat-label">Libros</span>
            </div>
            <div className="stat">
              <span className="stat-number">5,000+</span>
              <span className="stat-label">Usuarios</span>
            </div>
            <div className="stat">
              <span className="stat-number">99%</span>
              <span className="stat-label">Satisfacci√≥n</span>
            </div>
          </div>
        </div>

        {/* Right Panel - Login Form */}
        <div className="login-right">
          <div className="login-form-container">
            <div className="form-header">
              <h2>Iniciar Sesi√≥n</h2>
              <p>Ingresa tus credenciales para continuar</p>
            </div>

            {loginError && (
              <div className="alert alert-error">
                <span className="alert-icon">‚ö†Ô∏è</span>
                <span className="alert-text">{loginError}</span>
              </div>
            )}

            <form onSubmit={handleSubmit} className="login-form">
              <div className="form-group">
                <label htmlFor="email" className="form-label">
                  Email
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className={`form-input ${errors.email ? 'error' : ''}`}
                  placeholder="usuario@ejemplo.com"
                  disabled={loading}
                />
                {errors.email && (
                  <span className="error-message">{errors.email}</span>
                )}
              </div>

              <div className="form-group">
                <label htmlFor="password" className="form-label">
                  Contrase√±a
                </label>
                <div className="password-input-container">
                  <input
                    type={showPassword ? "text" : "password"}
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    className={`form-input ${errors.password ? 'error' : ''}`}
                    placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                    disabled={loading}
                  />
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                    disabled={loading}
                  >
                    {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                  </button>
                </div>
                {errors.password && (
                  <span className="error-message">{errors.password}</span>
                )}
              </div>

              <div className="form-options">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="rememberMe"
                    checked={formData.rememberMe}
                    onChange={handleChange}
                    disabled={loading}
                  />
                  <span className="checkbox-text">Recordarme</span>
                </label>
                <button
                  type="button"
                  className="forgot-password"
                  onClick={handleForgotPassword}
                  disabled={loading}
                >
                  ¬øOlvidaste tu contrase√±a?
                </button>
              </div>

              <button
                type="submit"
                className="btn btn-primary btn-block"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <LoadingSpinner size="small" color="light" />
                    Iniciando sesi√≥n...
                  </>
                ) : (
                  'Iniciar Sesi√≥n'
                )}
              </button>
            </form>

            {/* Demo Accounts */}
            <div className="demo-accounts">
              <p className="demo-label">Cuentas de demostraci√≥n:</p>
              <div className="demo-buttons">
                <button
                  type="button"
                  className="btn btn-outline btn-sm"
                  onClick={() => handleDemoLogin('USER')}
                  disabled={loading}
                >
                  üë§ Usuario
                </button>
                <button
                  type="button"
                  className="btn btn-outline btn-sm"
                  onClick={() => handleDemoLogin('LIBRARIAN')}
                  disabled={loading}
                >
                  üìö Bibliotecario
                </button>
                <button
                  type="button"
                  className="btn btn-outline btn-sm"
                  onClick={() => handleDemoLogin('ADMIN')}
                  disabled={loading}
                >
                  üëë Administrador
                </button>
              </div>
            </div>

            {/* Social Login */}
            <div className="social-login">
              <div className="divider">
                <span className="divider-text">o continuar con</span>
              </div>
              <div className="social-buttons">
                <button
                  type="button"
                  className="btn btn-outline btn-social"
                  disabled={loading}
                >
                  <span className="social-icon">üîµ</span>
                  <span className="social-text">Google</span>
                </button>
                <button
                  type="button"
                  className="btn btn-outline btn-social"
                  disabled={loading}
                >
                  <span className="social-icon">üîµ</span>
                  <span className="social-text">Microsoft</span>
                </button>
              </div>
            </div>

            {/* Register Link */}
            <div className="register-section">
              <p className="register-text">
                ¬øNo tienes una cuenta?{' '}
                <Link to="/register" className="register-link">
                  Reg√≠strate aqu√≠
                </Link>
              </p>
            </div>
          </div>

          {/* Footer */}
          <div className="login-footer">
            <p className="footer-text">
              ¬© 2024 Biblioteca Digital. Todos los derechos reservados.
            </p>
            <div className="footer-links">
              <Link to="/privacy" className="footer-link">
                Privacidad
              </Link>
              <Link to="/terms" className="footer-link">
                T√©rminos
              </Link>
              <Link to="/help" className="footer-link">
                Ayuda
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
